import React from 'react'

export const CategoryList = (props) => {
  const { categories, setSelectedCategory } = props
  return (
    <main className='main'>
      <div className='header'>
        <h1 className='title'>Time for Trivia!</h1>
      </div>
      <table className='container'>
        <thead>
          <tr>
                  <th>Select a Category</th>
              </tr>
          </thead>
        {categories.map(category => {
          return (
            <tbody key={category.id} className='table is-bordered is-hoverable'>
              <tr>
                <td><p className='category-name'><strong>{category.name}</strong></p></td>
                <td><button className='button is-link' onClick={() => setSelectedCategory(category)}>Select Category</button></td>
              </tr>
            </tbody>
          )
        })}
      </table>
    </main>
  )
}
