import React from 'react'

export const CategoryList = (props) => {
  const { categories, setSelectedCategory } = props
  return (
    <main className='main'>
      <div className='header'>
        <h1 className='title'>Trivia Game</h1>
        <h2 className='title'>Select a Category</h2>
      </div>
      <div className='container'>
        {categories.map(category => {
          return (
            <div key={category.id} className='table is-striped'>
              <tbody>
                <tr>
                  <td><p className='category-name'><strong>{category.name}</strong></p></td>
                  <td><button className='button is-link' onClick={() => setSelectedCategory(category)}>Select Category</button></td>
                </tr>
              </tbody>
            </div>
          )
        })}
      </div>
    </main>
  )
}
